import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import sanjay from '../../assets/img5.jpg'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
	const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
	const [conversations, setConversations] = useState([])
	const [messages, setMessages] = useState({})
	const [message, setMessage] = useState('')
	const [users, setUsers] = useState([])
	const [socket, setSocket] = useState(null)
	const messageRef = useRef(null)

	useEffect(() => {
		setSocket(io('http://localhost:8080'))
	}, [])

	useEffect(() => {
		socket?.emit('addUser', user?.id);
		socket?.on('getUsers', users => {
			console.log('activeUsers :>> ', users);
		})
		socket?.on('getMessage', data => {
			setMessages(prev => ({
				...prev,
				messages: [...prev.messages, { user: data.user, message: data.message }]
			}))
		})
	}, [socket])

	useEffect(() => {
		messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
	}, [messages?.messages])

	useEffect(() => {
		const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
		const fetchConversations = async () => {
			const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setConversations(resData)
		}
		fetchConversations()
	}, [])

	useEffect(() => {
		const fetchUsers = async () => {
			const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setUsers(resData)
		}
		fetchUsers()
	}, [])

	const fetchMessages = async (conversationId, receiver) => {
		const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
			}
		});
		const resData = await res.json()
		setMessages({ messages: resData, receiver, conversationId })
	}

	const sendMessage = async (e) => {
		setMessage('')
		socket?.emit('sendMessage', {
			senderId: user?.id,
			receiverId: messages?.receiver?.receiverId,
			message,
			conversationId: messages?.conversationId
		});
		const res = await fetch(`http://localhost:8000/api/message`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				conversationId: messages?.conversationId,
				senderId: user?.id,
				message,
				receiverId: messages?.receiver?.receiverId
			})
		});
	}

	return (
		<div className='w-screen flex'>
		<div className='w-[25%] h-screen bg-teal-700 overflow-scroll transition-all duration-500'>
	<div className='flex items-center my-8 mx-14 transition-all duration-300 hover:bg-teal-600 p-4 rounded-lg transform hover:scale-105 hover:shadow-xl'>
		<div><img src={sanjay} width={90} height={90} className='border border-primary p-[2px] rounded-full shadow-lg transition-all duration-300 hover:shadow-2xl' /></div>
		<div className='ml-8'>
			<h3 className='text-2xl text-white font-bold transition-all duration-300 hover:text-teal-100'>{user?.fullName}</h3>
			<p className='text-lg font-light text-teal-200 transition-all duration-300 hover:text-teal-100'>My Account</p>
		</div>
	</div>
	<hr className='border-teal-300' />
	<div className='mx-10 mt-10'>
		<div className='text-teal-200 text-lg font-semibold transition-all duration-300 hover:text-teal-100 '>Messages</div>
		<div>
			{
				conversations.length > 0 ?
					conversations.map(({ conversationId, user }) => {
						return (
							<div className='flex items-center py-5 border-b border-b-teal-300 transition-all duration-300 hover:bg-teal-600 cursor-pointer transform hover:scale-105 hover:shadow-md rounded-md'
								onClick={() => fetchMessages(conversationId, user)}>
								<div><img src={Img1} className="w-[60px] h-[60px] rounded-full p-[2px] border border-teal-400 shadow-md transition-all duration-300 hover:shadow-lg hover:scale-110" /></div>
								<div className='ml-6'>
									<h3 className='text-lg font-semibold text-white transition-all duration-300 hover:text-teal-100'>{user?.fullName}</h3>
									<p className='text-sm font-light text-teal-200 transition-all duration-300 hover:text-teal-100'>{user?.email}</p>
								</div>
							</div>
						)
					}) : <div className='text-center text-lg font-semibold text-teal-200 mt-24'>No Conversations</div>
			}
		</div>
	</div>
</div>

<div className='h-[75%] w-full overflow-scroll shadow-sm'>
	<div className='p-14'>
		{
			messages?.messages?.length > 0 ?
				messages.messages.map(({ message, user: { id } = {} }, index) => {
					return (
						<div 
							className={`max-w-[40%] rounded-b-xl p-4 mb-6 
								${id === user?.id ? 'bg-primary text-white rounded-tl-xl ml-auto' : 'bg-secondary rounded-tr-xl'} 
								transition-all duration-300 transform ease-in-out 
								${index % 2 === 0 ? 'animate-fadeIn' : 'animate-fadeInReverse'}`}
						>
							{message}
						</div>
					)
				}) : <div className='text-center text-lg font-semibold mt-24'>No Messages or No Conversation Selected</div>
		}
	</div>
</div>

{/* People Section with Hover Animation */}
<div className='w-[25%] h-screen bg-light px-8 py-16 overflow-scroll'>
	<div className='text-primary text-lg'>People</div>
	<div>
		{
			users.length > 0 ?
				users.map(({ userId, user }) => {
					return (
						<div 
							className='flex items-center py-8 border-b border-b-gray-300 
							transition-all duration-300 ease-in-out hover:bg-teal-100 hover:scale-105 rounded-lg'>
							<div className='cursor-pointer flex items-center' onClick={() => fetchMessages('new', user)}>
								<div><img src={Img1} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary transition-all duration-300 hover:shadow-lg" /></div>
								<div className='ml-6'>
									<h3 className='text-lg font-semibold'>{user?.fullName}</h3>
									<p className='text-sm font-light text-gray-600'>{user?.email}</p>
								</div>
							</div>
						</div>
					)
			}) : <div className='text-center text-lg font-semibold mt-24'>No Conversations</div>
		}
	</div>
</div>

			<div className='w-[25%] h-screen bg-light px-8 py-16 overflow-scroll'>
				<div className='text-primary text-lg'>People</div>
				<div>
					{
						users.length > 0 ?
							users.map(({ userId, user }) => {
								return (
									<div className='flex items-center py-8 border-b border-b-gray-300'>
										<div className='cursor-pointer flex items-center' onClick={() => fetchMessages('new', user)}>
											<div><img src={Img1} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" /></div>
											<div className='ml-6'>
												<h3 className='text-lg font-semibold'>{user?.fullName}</h3>
												<p className='text-sm font-light text-gray-600'>{user?.email}</p>
											</div>
										</div>
									</div>
								)
						}) : <div className='text-center text-lg font-semibold mt-24'>No Conversations</div>
					}
				</div>
			</div>
		</div>
	)
}

export default Dashboard
