import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import sanjay from '../../assets/img5.jpg'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
	const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
	const [conversations, setConversations] = useState([])
	const [messages, setMessages] = useState({})
	const [message, setMessage] = useState('')
	const [users, setUsers] = useState([])
	const [socket, setSocket] = useState(null)
	const messageRef = useRef(null)

	useEffect(() => {
		setSocket(io('http://localhost:8080'))
	}, [])

	useEffect(() => {
		socket?.emit('addUser', user?.id);
		socket?.on('getUsers', users => {
			console.log('activeUsers :>> ', users);
		})
		socket?.on('getMessage', data => {
			setMessages(prev => ({
				...prev,
				messages: [...prev.messages, { user: data.user, message: data.message }]
			}))
		})
	}, [socket])

	useEffect(() => {
		messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
	}, [messages?.messages])

	useEffect(() => {
		const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
		const fetchConversations = async () => {
			const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setConversations(resData)
		}
		fetchConversations()
	}, [])

	useEffect(() => {
		const fetchUsers = async () => {
			const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setUsers(resData)
		}
		fetchUsers()
	}, [])

	const fetchMessages = async (conversationId, receiver) => {
		const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
			}
		});
		const resData = await res.json()
		setMessages({ messages: resData, receiver, conversationId })
	}

	const sendMessage = async (e) => {
		setMessage('')
		socket?.emit('sendMessage', {
			senderId: user?.id,
			receiverId: messages?.receiver?.receiverId,
			message,
			conversationId: messages?.conversationId
		});
		const res = await fetch(`http://localhost:8000/api/message`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				conversationId: messages?.conversationId,
				senderId: user?.id,
				message,
				receiverId: messages?.receiver?.receiverId
			})
		});
	}

	return (
		<div className="w-screen flex flex-col lg:flex-row">
		{/* Left Sidebar */}
		<div className="w-full lg:w-[25%] lg:h-screen bg-slate-200 overflow-hidden">
		  <div className="flex items-center my-8 mx-14">
			<div>
			  <img
				src={sanjay}
				width={75}
				height={75}
				className="border border-primary p-[2px] rounded-full"
			  />
			</div>
			<div className="ml-8">
			  <h3 className="text-2xl">{user?.fullName}</h3>
			  <p className="text-lg font-light">My Account</p>
			</div>
		  </div>
		  <hr />
		  <div className="mx-14 mt-10">
			<div className="text-primary text-lg">Messages</div>
			<div>
			  {conversations.length > 0 ? (
				conversations.map(({ conversationId, user }) => (
				  <div
					className="flex items-center py-8 border-b-2 border-gray-300"
					key={conversationId}
				  >
					<div
					  className="cursor-pointer flex items-center"
					  onClick={() => fetchMessages(conversationId, user)}
					>
					  <div>
						<img
						  src={Img1}
						  className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
						/>
					  </div>
					  <div className="ml-6">
						<h3 className="text-lg font-semibold">{user?.fullName}</h3>
						<p className="text-sm font-light text-gray-600">
						  {user?.email}
						</p>
					  </div>
					</div>
				  </div>
				))
			  ) : (
				<div className="text-center text-lg font-semibold mt-24">
				  No Conversations
				</div>
			  )}
			</div>
		  </div>
		</div>

		{/* Center Content */}
		<div className="w-full lg:w-[50%] lg:h-screen bg-white flex flex-col items-center overflow-hidden">
		  {messages?.receiver?.fullName && (
			<div className="w-[90%] lg:w-[75%] bg-blue-200 h-[80px] my-14 rounded-full flex items-center px-14 py-2">
			  <div className="cursor-pointer">
				<img
				  src={Img1}
				  width={60}
				  height={60}
				  className="rounded-full"
				/>
			  </div>
			  <div className="ml-6 mr-auto">
				<h3 className="text-lg">{messages?.receiver?.fullName}</h3>
				<p className="text-sm font-light text-gray-600">
				  {messages?.receiver?.email}
				</p>
			  </div>
			  <div className="cursor-pointer">
				<svg
				  xmlns="http://www.w3.org/2000/svg"
				  className="icon icon-tabler icon-tabler-phone-outgoing"
				  width="24"
				  height="24"
				  viewBox="0 0 24 24"
				  strokeWidth="1.5"
				  stroke="black"
				  fill="none"
				  strokeLinecap="round"
				  strokeLinejoin="round"
				>
				  <path stroke="none" d="M0 0h24v24H0z" fill="none" />
				  <path d="M5 4h4l2 5l-2.5 1.5a11 11 0 0 0 5 5l1.5 -2.5l5 2v4a2 2 0 0 1 -2 2a16 16 0 0 1 -15 -15a2 2 0 0 1 2 -2" />
				  <line x1="15" y1="9" x2="20" y2="4" />
				  <polyline points="16 4 20 4 20 8" />
				</svg>
			  </div>
			</div>
		  )}
		  <div className="h-[75%] w-full overflow-hidden shadow-sm">
			<div className="p-14">
			  {messages?.messages?.length > 0 ? (
				messages.messages.map(({ message, user: { id } = {} }) => (
				  <div
					key={message}
					className={`max-w-[80%] md:max-w-[40%] rounded-b-xl p-4 mb-6 ${
					  id === user?.id
						? "bg-primary text-white rounded-tl-xl ml-auto"
						: "bg-secondary rounded-tr-xl"
					}`}
				  >
					{message}
				  </div>
				))
			  ) : (
				<div className="text-center text-lg font-semibold mt-24">
				  No Messages or No Conversation Selected
				</div>
			  )}
			</div>
		  </div>
		</div>

		{/* Right Sidebar */}
		<div className="w-full lg:w-[25%] lg:h-screen bg-slate-200 px-4 md:px-8 py-8 lg:py-16 overflow-hidden">
		  <div className="text-primary text-lg">People</div>
		  <div>
			{users.length > 0 ? (
			  users.map(({ userId, user }) => (
				<div
				  className="flex items-center py-6 border-b-2 border-gray-300"
				  key={userId}
				>
				  <div
					className="cursor-pointer flex items-center"
					onClick={() => fetchMessages("new", user)}
				  >
					<div>
					  <img
						src={Img1}
						className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary"
					  />
					</div>
					<div className="ml-6">
					  <h3 className="text-lg font-semibold">{user?.fullName}</h3>
					  <p className="text-sm font-light text-gray-600">
						{user?.email}
					  </p>
					</div>
				  </div>
				</div>
			  ))
			) : (
			  <div className="text-center text-lg font-semibold mt-24">
				No Conversations
			  </div>
			)}
		  </div>
		</div>
	  </div>

	)
}

export default Dashboard
