import { useState } from "react";
import Button from "../../components/Button";
import Input from "../../components/Input";
import { useNavigate } from "react-router-dom";

const Form = ({ isSignInPage = true }) => {
  const [data, setData] = useState({
    ...(!isSignInPage && { fullName: "", profilePicture: null }), // Add profile picture for sign-up
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setData({ ...data, profilePicture: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const endpoint = `http://localhost:8000/api/${isSignInPage ? "login" : "register"}`;

    let body;
    let headers;

    if (!isSignInPage) {
      // Use FormData for file uploads during registration
      body = new FormData();
      body.append("fullName", data.fullName);
      body.append("email", data.email);
      body.append("password", data.password);
      if (data.profilePicture) {
        body.append("profilePicture", data.profilePicture);
      }
      headers = {}; // FormData automatically sets the content type
    } else {
      // For login, send JSON data
      body = JSON.stringify({
        email: data.email,
        password: data.password,
      });
      headers = { "Content-Type": "application/json" };
    }

    try {
      const res = await fetch(endpoint, {
        method: "POST",
        headers,
        body,
      });

      if (res.status === 400) {
        alert("Invalid credentials");
      } else {
        const resData = await res.json();
        if (resData.token) {
          localStorage.setItem("user:token", resData.token);
          localStorage.setItem("user:detail", JSON.stringify(resData.user));
          navigate("/");
        }
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <div className="bg-light h-screen flex items-center justify-center">
      <div className="bg-white w-[600px] h-auto shadow-lg rounded-lg flex flex-col justify-center items-center p-8">
        <div className="text-4xl font-extrabold">Welcome {isSignInPage && "Back"}</div>
        <div className="text-xl font-light mb-10">
          {isSignInPage ? "Sign in to get explored" : "Sign up to get started"}
        </div>
        <form className="flex flex-col items-center w-full" onSubmit={(e) => handleSubmit(e)}>
          {!isSignInPage && (
            <>
              <Input
                label="Full name"
                name="fullName"
                placeholder="Enter your full name"
                className="mb-6 w-[75%]"
                value={data.fullName}
                onChange={(e) => setData({ ...data, fullName: e.target.value })}
              />
              <div className="mb-6 w-[75%]">
                <label className="block text-sm font-medium text-gray-700 mb-2">Profile Picture</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 focus:outline-none"
                />
              </div>
            </>
          )}
          <Input
            label="Email address"
            type="email"
            name="email"
            placeholder="Enter your email"
            className="mb-6 w-[75%]"
            value={data.email}
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
          <Input
            label="Password"
            type="password"
            name="password"
            placeholder="Enter your password"
            className="mb-10 w-[75%]"
            value={data.password}
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
          <Button label={isSignInPage ? "Sign in" : "Sign up"} type="submit" className="w-[75%] mb-2" />
        </form>
        <div>
          {isSignInPage ? "Didn't have an account?" : "Already have an account?"}{" "}
          <span
            className="text-primary cursor-pointer underline"
            onClick={() => navigate(`/users/${isSignInPage ? "sign_up" : "sign_in"}`)}
          >
            {isSignInPage ? "Sign up" : "Sign in"}
          </span>
        </div>
      </div>
    </div>
  );
};

export default Form;
