import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import sanjay from '../../assets/img5.jpg'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
	const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
	const [conversations, setConversations] = useState([])
	const [messages, setMessages] = useState({})
	const [message, setMessage] = useState('')
	const [users, setUsers] = useState([])
	const [socket, setSocket] = useState(null)
	const messageRef = useRef(null)

	useEffect(() => {
		setSocket(io('http://localhost:8080'))
	}, [])

	useEffect(() => {
		socket?.emit('addUser', user?.id);
		socket?.on('getUsers', users => {
			console.log('activeUsers :>> ', users);
		})
		socket?.on('getMessage', data => {
			setMessages(prev => ({
				...prev,
				messages: [...prev.messages, { user: data.user, message: data.message }]
			}))
		})
	}, [socket])

	useEffect(() => {
		messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
	}, [messages?.messages])

	useEffect(() => {
		const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
		const fetchConversations = async () => {
			const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setConversations(resData)
		}
		fetchConversations()
	}, [])

	useEffect(() => {
		const fetchUsers = async () => {
			const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setUsers(resData)
		}
		fetchUsers()
	}, [])

	const fetchMessages = async (conversationId, receiver) => {
		const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
			}
		});
		const resData = await res.json()
		setMessages({ messages: resData, receiver, conversationId })
	}

	const sendMessage = async (e) => {
		setMessage('')
		socket?.emit('sendMessage', {
			senderId: user?.id,
			receiverId: messages?.receiver?.receiverId,
			message,
			conversationId: messages?.conversationId
		});
		const res = await fetch(`http://localhost:8000/api/message`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				conversationId: messages?.conversationId,
				senderId: user?.id,
				message,
				receiverId: messages?.receiver?.receiverId
			})
		});
	}

	return (
		<div className='flex w-screen h-screen bg-gray-100'>
			{/* Left Sidebar */}
			<div className='w-[25%] h-full bg-indigo-800 p-6'>
				<div className='flex items-center space-x-4 p-4 transition-all duration-300 hover:bg-indigo-700 rounded-lg cursor-pointer'>
					<img src={sanjay} width={80} height={80} className='border-2 border-indigo-600 p-1 rounded-full shadow-lg' />
					<div>
						<h3 className='text-white text-2xl font-semibold'>{user?.fullName}</h3>
						<p className='text-indigo-300 text-sm'>{user?.email}</p>
					</div>
				</div>
				<hr className='border-indigo-600 my-4' />
				<div className='text-indigo-300 text-lg font-semibold'>Messages</div>
				<div>
					{conversations.length > 0 ? conversations.map(({ conversationId, user }) => (
						<div className='flex items-center py-4 border-b border-indigo-500 transition-all duration-300 hover:bg-indigo-700 cursor-pointer rounded-md' onClick={() => fetchMessages(conversationId, user)}>
							<img src={Img1} className='w-[60px] h-[60px] rounded-full p-[2px] border border-indigo-400' />
							<div className='ml-6'>
								<h3 className='text-white font-semibold'>{user?.fullName}</h3>
								<p className='text-sm text-indigo-300'>{user?.email}</p>
							</div>
						</div>
					)) : <div className='text-center text-indigo-300 mt-12'>No Conversations</div>}
				</div>
			</div>

			{/* Center Chat */}
			<div className='w-[50%] h-full bg-white flex flex-col items-center'>
				{messages?.receiver?.fullName &&
					<div className='w-[75%] bg-indigo-100 h-[80px] my-4 rounded-full flex items-center px-4'>
						<img src={Img1} width={60} height={60} className='rounded-full' />
						<div className='ml-6'>
							<h3 className='text-lg font-semibold'>{messages?.receiver?.fullName}</h3>
							<p className='text-sm font-light text-indigo-600'>{messages?.receiver?.email}</p>
						</div>
					</div>
				}
				{/* Chat Messages */}
				<div className='h-[75%] w-full overflow-scroll px-4'>
					<div className='space-y-4'>
						{messages?.messages?.length > 0 ? messages.messages.map(({ message, user: { id } = {} }) => (
							<div key={id} className={`p-4 mb-4 max-w-[40%] rounded-b-xl ${id === user?.id ? 'bg-indigo-600 text-white ml-auto' : 'bg-indigo-50 text-black'} shadow-md`}>
								{message}
							</div>
						)) : <div className='text-center text-lg font-semibold mt-12 text-indigo-500'>No Messages or No Conversation Selected</div>}
						<div ref={messageRef}></div>
					</div>
				</div>

				{/* Message Input */}
				{messages?.receiver?.fullName &&
					<div className='w-full p-4 flex items-center'>
						<Input placeholder='Type a message...' value={message} onChange={(e) => setMessage(e.target.value)} className='w-[75%] bg-indigo-50 p-3 rounded-full border border-indigo-300 focus:outline-none focus:ring-2 focus:ring-indigo-500' />
						<div className={`ml-4 p-2 cursor-pointer bg-indigo-500 rounded-full ${!message && 'opacity-50 cursor-not-allowed'}`} onClick={sendMessage} disabled={!message}>
							<svg xmlns="http://www.w3.org/2000/svg" className='w-6 h-6 text-white' viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
								<path d="M21 3L10 14l-3-3m3 3l9 9" />
							</svg>
						</div>
					</div>
				}
			</div>

			{/* Right Sidebar */}
			<div className='w-[25%] h-full bg-indigo-50 px-6 py-8'>
				<div className='text-indigo-700 text-lg font-semibold'>People</div>
				<div>
					{users.length > 0 ? users.map(({ userId, user }) => (
						<div key={userId} className='flex items-center py-4 border-b border-indigo-300 cursor-pointer hover:bg-indigo-200'>
							<img src={Img1} className='w-[60px] h-[60px] rounded-full' />
							<div className='ml-6'>
								<h3 className='text-indigo-700 font-semibold'>{user?.fullName}</h3>
								<p className='text-sm text-indigo-500'>{user?.email}</p>
							</div>
						</div>
					)) : <div className='text-center text-indigo-500 mt-12'>No People Available</div>}
				</div>
			</div>
		</div>
	)

}

export default Dashboard
