import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import sanjay from '../../assets/img5.jpg'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
  const [conversations, setConversations] = useState([])
  const [messages, setMessages] = useState({})
  const [message, setMessage] = useState('')
  const [users, setUsers] = useState([])
  const [socket, setSocket] = useState(null)
  const messageRef = useRef(null)

  useEffect(() => {
    setSocket(io('http://localhost:8080'))
  }, [])

  useEffect(() => {
    socket?.emit('addUser', user?.id)
    socket?.on('getUsers', users => {
      console.log('activeUsers :>> ', users)
    })
    socket?.on('getMessage', data => {
      setMessages(prev => ({
        ...prev,
        messages: [...prev.messages, { user: data.user, message: data.message }]
      }))
    })
  }, [socket])

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
  }, [messages?.messages])

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
    const fetchConversations = async () => {
      const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      })
      const resData = await res.json()
      setConversations(resData)
    }
    fetchConversations()
  }, [])

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      })
      const resData = await res.json()
      setUsers(resData)
    }
    fetchUsers()
  }, [])

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' }
    })
    const resData = await res.json()
    setMessages({ messages: resData, receiver, conversationId })
  }

  const sendMessage = async () => {
    setMessage('')
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    })
    const res = await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    })
  }

  return (
	<div className="flex flex-col h-screen bg-gray-100">

	  {/* Top Section - User Profile and Receiver Info */}
	  <div className="flex flex-col bg-[#075E54] text-white p-4 shadow-md">
		{/* User Profile */}
		<div className="flex items-center space-x-4 border-b border-white">
		  <img src={sanjay} width={60} height={60} className="border border-white rounded-full" />
		  <div>
			<h3 className="text-2xl font-bold text-white">{user?.fullName}</h3>
			<p className="text-sm text-gray-300">My Account</p>
		  </div>
		</div>

		{/* Receiver Info (if a conversation is selected) */}
		{messages?.receiver?.fullName && (
		  <div className="flex items-center space-x-4 mt-4">
			<img src={Img1} width={50} height={50} className="rounded-full" />
			<div>
			  <h3 className="text-xl font-semibold">{messages?.receiver?.fullName}</h3>
			  <p className="text-sm text-gray-300">{messages?.receiver?.email}</p>
			</div>
		  </div>
		)}
	  </div>

	  {/* Message Area */}
	  {messages?.receiver?.fullName ? (
		<div className="flex-1 overflow-y-auto p-4">

		  {/* Message List */}
		  <div>
			{messages?.messages?.length > 0 ? (
			  messages.messages.map(({ message, user: { id } = {} }) => (
				<div
				  key={id}
				  className={`max-w-[60%] p-4 rounded-lg mb-3 ${id === user?.id ? 'bg-[#25D366] text-white ml-auto' : 'bg-[#ECE5DD] text-black'}`}
				>
				  {message}
				</div>
			  ))
			) : (
			  <div className="text-center text-lg text-gray-400">No Messages</div>
			)}
		  </div>

		  {/* Message Input */}
		  <div className="flex items-center p-4 border-t border-gray-300">
			<Input
			  placeholder="Type a message..."
			  value={message}
			  onChange={(e) => setMessage(e.target.value)}
			  className="w-full p-3 rounded-lg bg-[#ECE5DD] text-black"
			/>
			<div
			  className={`ml-4 p-3 cursor-pointer bg-[#25D366] rounded-full text-white hover:bg-[#128C7E] ${!message && 'pointer-events-none'}`}
			  onClick={sendMessage}
			>
			  <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-send" width="24" height="24" viewBox="0 0 24 24" stroke-width="1.5" stroke="#fff" fill="none" stroke-linecap="round" stroke-linejoin="round">
				<path stroke="none" d="M0 0h24v24H0z" fill="none" />
				<line x1="10" y1="14" x2="21" y2="3" />
				<path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
			  </svg>
			</div>
		  </div>
		</div>
	  ) : (
		// If no conversation is selected, show "Back to Conversations" or "Select a Conversation"
		<div className="flex-1 flex justify-center items-center">
		  <p className="text-lg text-gray-500">Select a conversation to start chatting.</p>
		</div>
	  )}
	</div>
  )
  }


export default Dashboard
