import { useState } from "react";
import Button from "../../components/Button";
import Input from "../../components/Input";
import { useNavigate } from "react-router-dom";

const Form = ({ isSignInPage = true }) => {
    const [data, setData] = useState({
        ...(!isSignInPage && { fullName: "" }),
        email: "",
        password: "",
        profilePhoto: null,
    });
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        Object.keys(data).forEach((key) => {
            formData.append(key, data[key]);
        });

        const endpoint = `http://localhost:8000/api/${isSignInPage ? "login" : "register"}`;

        const res = await fetch(endpoint, {
            method: "POST",
            body: formData,
        });

        if (res.status === 400) {
            alert("Invalid credentials");
        } else {
            const resData = await res.json();
            if (resData.token) {
                localStorage.setItem("user:token", resData.token);
                localStorage.setItem("user:detail", JSON.stringify(resData.user));
                navigate("/");
            }
        }
    };

    const toggleFormType = () => {
        navigate(`/users/${isSignInPage ? "sign_up" : "sign_in"}`);
    };

    return (
        <div className="bg-light h-screen flex items-center justify-center">
            <div className="bg-white w-[600px] h-[800px] shadow-lg rounded-lg flex flex-col justify-center items-center">
                <h1 className="text-4xl font-extrabold">
                    Welcome {isSignInPage ? "Back" : ""}
                </h1>
                <p className="text-xl font-light mb-14">
                    {isSignInPage
                        ? "Sign in to get explored"
                        : "Sign up to get started"}
                </p>

                <form className="flex flex-col items-center w-full" onSubmit={handleSubmit}>
                    {!isSignInPage && (
                        <Input
                            label="Full name"
                            name="name"
                            placeholder="Enter your full name"
                            className="mb-6 w-[75%]"
                            value={data.fullName}
                            onChange={(e) => setData({ ...data, fullName: e.target.value })}
                        />
                    )}
                    <Input
                        label="Email address"
                        type="email"
                        name="email"
                        placeholder="Enter your email"
                        className="mb-6 w-[75%]"
                        value={data.email}
                        onChange={(e) => setData({ ...data, email: e.target.value })}
                    />
                    <Input
                        label="Password"
                        type="password"
                        name="password"
                        placeholder="Enter your Password"
                        className="mb-14 w-[75%]"
                        value={data.password}
                        onChange={(e) => setData({ ...data, password: e.target.value })}
                    />
                    {!isSignInPage && (
                        <div className="mb-6 w-[75%]">
                            <label className="block mb-2 text-sm font-medium text-gray-700">
                                Profile Photo
                            </label>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => setData({ ...data, profilePhoto: e.target.files[0] })}
                                className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50"
                            />
                        </div>
                    )}
                    <Button
                        label={isSignInPage ? "Sign in" : "Sign up"}
                        type="submit"
                        className="w-[75%] mb-2"
                    />
                </form>

                <div>
                    {isSignInPage
                        ? "Didn't have an account?"
                        : "Already have an account?"}{" "}
                    <span
                        className="text-primary cursor-pointer underline"
                        onClick={toggleFormType}
                    >
                        {isSignInPage ? "Sign up" : "Sign in"}
                    </span>
                </div>
            </div>
        </div>
    );
};

export default Form;
