import { useEffect, useRef, useState } from 'react'
import Img1 from '../../assets/img1.jpg'
import sanjay from '../../assets/img5.jpg'
import Input from '../../components/Input'
import { io } from 'socket.io-client'

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')))
  const [conversations, setConversations] = useState([])
  const [messages, setMessages] = useState({})
  const [message, setMessage] = useState('')
  const [users, setUsers] = useState([])
  const [socket, setSocket] = useState(null)
  const messageRef = useRef(null)

  useEffect(() => {
    setSocket(io('http://localhost:8080'))
  }, [])

  useEffect(() => {
    socket?.emit('addUser', user?.id)
    socket?.on('getUsers', users => {
      console.log('activeUsers :>> ', users)
    })
    socket?.on('getMessage', data => {
      setMessages(prev => ({
        ...prev,
        messages: [...prev.messages, { user: data.user, message: data.message }]
      }))
    })
  }, [socket])

  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' })
  }, [messages?.messages])

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
    const fetchConversations = async () => {
      const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      })
      const resData = await res.json()
      setConversations(resData)
    }
    fetchConversations()
  }, [])

  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      })
      const resData = await res.json()
      setUsers(resData)
    }
    fetchUsers()
  }, [])

  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' }
    })
    const resData = await res.json()
    setMessages({ messages: resData, receiver, conversationId })
  }

  const sendMessage = async () => {
    setMessage('')
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId
    })
    const res = await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId
      })
    })
  }

  return (
    <div className='w-screen h-screen flex bg-gray-100'>
      {/* Left Side - Conversations */}
      <div className='w-full md:w-[35%] lg:w-[25%] bg-secondary overflow-y-auto p-6'>
        {/* User Profile */}
        <div className='flex items-center space-x-4 mb-6'>
          <img src={sanjay} width={75} height={75} className='border border-primary p-[2px] rounded-full' />
          <div>
            <h3 className='text-2xl text-white font-semibold'>{user?.fullName}</h3>
            <p className='text-lg text-gray-400'>My Account</p>
          </div>
        </div>
        <hr className="my-4" />
        {/* Conversations */}
        <div>
          <h4 className='text-primary font-semibold text-lg mb-4'>Messages</h4>
          {conversations.length > 0 ? conversations.map(({ conversationId, user }) => (
            <div
              key={conversationId}
              className='flex flex-col items-start py-4 border-b border-b-gray-300 hover:bg-gray-200 cursor-pointer transition-all rounded-md mb-4'
              onClick={() => fetchMessages(conversationId, user)}
            >
              <img src={Img1} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" />
              <div className='ml-6 mt-2'>
                <h3 className='text-lg font-semibold text-white'>{user?.fullName}</h3>
                <p className='text-sm text-gray-500'>{user?.email}</p>
              </div>
              <p className='mt-2 text-sm text-gray-300'>Conversation ID: {conversationId}</p>
            </div>
          )) : <div className='text-center text-lg text-gray-400 mt-24'>No Conversations</div>}
        </div>
      </div>

      {/* Middle Section - Chat */}
      <div className='w-full md:w-[60%] lg:w-[50%] bg-gradient-to-tl from-[#ff6a00] to-[#ffbb33] flex flex-col p-8'>
        {/* Receiver Info */}
        {messages?.receiver?.fullName && (
          <div className='w-full bg-primary h-[80px] flex items-center px-6 py-4 rounded-xl shadow-lg'>
            <img src={Img1} width={60} height={60} className="rounded-full" />
            <div className='ml-6'>
              <h3 className='text-lg text-white font-semibold'>{messages?.receiver?.fullName}</h3>
              <p className='text-sm text-gray-200'>{messages?.receiver?.email}</p>
            </div>
          </div>
        )}
        {/* Messages */}
        <div className='flex-1 overflow-y-auto px-6 py-4 bg-white rounded-lg shadow-lg mt-4'>
          {messages?.messages?.length > 0 ? messages.messages.map(({ message, user: { id } = {} }) => (
            <div
              key={id}
              className={`max-w-[60%] rounded-lg p-4 mb-4 ${id === user?.id ? 'bg-primary text-white ml-auto' : 'bg-gray-200 text-black'}`}
            >
              {message}
            </div>
          )) : <div className='text-center text-lg font-semibold text-gray-400'>No Messages or No Conversation Selected</div>}
        </div>

        {/* Message Input */}
        {messages?.receiver?.fullName && (
          <div className='w-full p-6 flex items-center mt-4'>
            <Input
              placeholder='Type a message...'
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className='w-full md:w-[75%] lg:w-[80%] p-4 border border-gray-300 rounded-lg shadow-sm'
            />
            <div
              className={`ml-4 p-3 cursor-pointer rounded-full ${!message && 'pointer-events-none'} hover:bg-primary hover:text-white transition-all`}
              onClick={sendMessage}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-send" width="24" height="24" viewBox="0 0 24 24" stroke-width="1.5" stroke="#fff" fill="none" stroke-linecap="round" stroke-linejoin="round">
                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                <line x1="10" y1="14" x2="21" y2="3" />
                <path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
              </svg>
            </div>
          </div>
        )}
      </div>

      {/* Right Side - People */}
      <div className='w-full md:w-[35%] lg:w-[25%] bg-light px-6 py-8 overflow-y-auto'>
        <h4 className='text-primary font-semibold text-lg mb-4'>Registered Users</h4>
        {users.length > 0 ? users.map(({ userId, user }) => (
          <div
            key={userId}
            className='flex items-center py-4 border-b border-b-gray-300 hover:bg-gray-200 cursor-pointer transition-all rounded-md'
            onClick={() => fetchMessages('new', user)}
          >
            <img src={Img1} className="w-[60px] h-[60px] rounded-full p-[2px] border border-primary" />
            <div className='ml-6'>
              <h3 className='text-lg font-semibold text-black'>{user?.fullName}</h3>
              <p className='text-sm text-gray-600'>{user?.email}</p>
            </div>
          </div>
        )) : <div className='text-center text-lg text-gray-400 mt-24'>No People</div>}
      </div>
    </div>
  )
}

export default Dashboard
