import { useEffect, useRef, useState } from 'react';
import Img1 from '../../assets/img1.jpg';
import sanjay from '../../assets/img5.jpg';
import Input from '../../components/Input';
import { io } from 'socket.io-client';

const Dashboard = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:detail')));
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState({});
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const messageRef = useRef(null);

  // Socket connection
  useEffect(() => {
    setSocket(io('http://localhost:8080'));
  }, []);

  useEffect(() => {
    socket?.emit('addUser', user?.id);
    socket?.on('getUsers', (users) => {
      console.log('activeUsers :>> ', users);
    });
    socket?.on('getMessage', (data) => {
      setMessages((prev) => ({
        ...prev,
        messages: [...prev.messages, { user: data.user, message: data.message }],
      }));
    });
  }, [socket]);

  // Auto-scroll for new messages
  useEffect(() => {
    messageRef?.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages?.messages]);

  // Fetch conversations
  useEffect(() => {
    const fetchConversations = async () => {
      const loggedInUser = JSON.parse(localStorage.getItem('user:detail'));
      const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
      const resData = await res.json();
      setConversations(resData);
    };
    fetchConversations();
  }, []);

  // Fetch users
  useEffect(() => {
    const fetchUsers = async () => {
      const res = await fetch(`http://localhost:8000/api/users/${user?.id}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
      const resData = await res.json();
      setUsers(resData);
    };
    fetchUsers();
  }, []);

  // Fetch messages
  const fetchMessages = async (conversationId, receiver) => {
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}?senderId=${user?.id}&&receiverId=${receiver?.receiverId}`, {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    });
    const resData = await res.json();
    setMessages({ messages: resData, receiver, conversationId });
  };

  // Send message
  const sendMessage = async () => {
    setMessage('');
    socket?.emit('sendMessage', {
      senderId: user?.id,
      receiverId: messages?.receiver?.receiverId,
      message,
      conversationId: messages?.conversationId,
    });
    await fetch(`http://localhost:8000/api/message`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        conversationId: messages?.conversationId,
        senderId: user?.id,
        message,
        receiverId: messages?.receiver?.receiverId,
      }),
    });
  };

  return (
    <div className="w-screen flex flex-wrap md:flex-nowrap">
      {/* Sidebar */}
      <div className="w-full md:w-[25%] h-screen bg-slate-200 overflow-y-auto">
        <div className="flex items-center my-8 mx-4 md:mx-14">
          <div>
            <img src={sanjay} width={75} height={75} className="border border-primary p-[2px] rounded-full" />
          </div>
          <div className="ml-4 md:ml-8">
            <h3 className="text-lg md:text-2xl">{user?.fullName}</h3>
            <p className="text-sm md:text-lg font-light">My Account</p>
          </div>
        </div>
        <hr />
        <div className="mx-4 md:mx-14 mt-6 md:mt-10">
          <div className="text-primary text-lg">Messages</div>
          <div>
            {conversations.length > 0 ? (
              conversations.map(({ conversationId, user }) => (
                <div
                  key={conversationId}
                  className="flex items-center py-4 md:py-8 border-b-2 border-gray-300 cursor-pointer"
                  onClick={() => fetchMessages(conversationId, user)}
                >
                  <div>
                    <img
                      src={Img1}
                      className="w-[50px] h-[50px] md:w-[60px] md:h-[60px] rounded-full p-[2px] border border-primary"
                    />
                  </div>
                  <div className="ml-4 md:ml-6">
                    <h3 className="text-sm md:text-lg font-semibold">{user?.fullName}</h3>
                    <p className="text-xs md:text-sm font-light text-gray-600">{user?.email}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center text-lg font-semibold mt-24">No Conversations</div>
            )}
          </div>
        </div>
      </div>

      {/* Chat Section */}
      <div className="w-full md:w-[50%] h-screen bg-white flex flex-col items-center">
        {/* Receiver Info */}
        {messages?.receiver?.fullName && (
          <div className="w-[90%] md:w-[75%] bg-blue-200 h-[80px] my-6 md:my-14 rounded-full flex items-center px-4 md:px-14 py-2">
            <div className="cursor-pointer">
              <img src={Img1} width={50} height={50} className="rounded-full" />
            </div>
            <div className="ml-4 md:ml-6 mr-auto">
              <h3 className="text-sm md:text-lg">{messages?.receiver?.fullName}</h3>
              <p className="text-xs md:text-sm font-light text-gray-600">{messages?.receiver?.email}</p>
            </div>
          </div>
        )}
        {/* Messages */}
        <div className="h-[70%] w-full overflow-y-auto shadow-sm">
          <div className="p-4 md:p-14">
            {messages?.messages?.length > 0 ? (
              messages.messages.map(({ message, user: { id } = {} }, index) => (
                <div
                  key={index}
                  className={`max-w-[70%] md:max-w-[40%] rounded-b-xl p-4 mb-4 md:mb-6 ${
                    id === user?.id
                      ? 'bg-primary text-white rounded-tl-xl ml-auto'
                      : 'bg-secondary rounded-tr-xl'
                  }`}
                >
                  {message}
                </div>
              ))
            ) : (
              <div className="text-center text-lg font-semibold mt-12 md:mt-24">
                No Messages or No Conversation Selected
              </div>
            )}
            <div ref={messageRef}></div>
          </div>
        </div>
        {/* Input */}
        {messages?.receiver?.fullName && (
          <div className="p-4 md:p-10 w-full flex items-center border-t">
            <Input
              placeholder="Type a message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="w-[85%] md:w-[75%]"
              inputClassName="p-2 md:p-4 border-0 shadow-md rounded-full bg-light focus:ring-0 focus:border-0 outline-none"
            />
            <div
              className={`ml-4 p-2 cursor-pointer bg-light rounded-full ${
                !message && 'pointer-events-none'
              }`}
              onClick={() => sendMessage()}
            >
              {/* Send Icon */}
            </div>
          </div>
        )}
      </div>

      {/* User Section */}
      <div className="w-full md:w-[25%] h-screen bg-slate-200 px-4 md:px-8 py-8 md:py-16 overflow-y-auto">
        {/* Other Active Users */}
        <div className="text-primary text-lg mb-8 md:mb-12">All Active Users</div>
        {users?.length > 0 ? (
          users.map((data) => (
            <div
              key={data?.id}
              className="flex items-center py-4 md:py-6 border-b-2 border-gray-300 cursor-pointer"
              onClick={() => fetchMessages(data?.conversationId, data)}
            >
              <div>
                <img src={Img1} className="w-[50px] h-[50px] md:w-[60px] md:h-[60px] rounded-full" />
              </div>
              <div className="ml-4 md:ml-6">
                <h3 className="text-sm md:text-lg font-semibold">{data?.fullName}</h3>
                <p className="text-xs md:text-sm font-light text-gray-600">{data?.email}</p>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center text-lg font-semibold mt-24">No Users Found</div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
